import { ThfChartPieData } from './thf-chart-pie-data.interface';
/**
 * @usedBy ThfChartComponent
 *
 * @description Interface da séries do Gráfico
 *
 */
export interface ThfBubbleChartSeries {
    /** Nome da série que será visível na legenda do gráfico. */
    name?: string;
    /** Array de Array de números, onde o gráfico for do tipo:
     * - bubble e verticalBubble deve ser informado array de 3 valores, valor x, valor y e tamanho do valor. <b>[[1, 2, 10]]</b>
     * - range, polar, scatter e scatterLine deve ser informado array de 2 valores, valor x e valor y. <b>[[1, 2]]</b>
     * - bullet e verticalBullet deve ser informado o valor corrente e o alvo. <b>[[2, 8]]</b>
     * - ohlc e candlestick deve ser informado array de 4 valores - open, high, low e close. <b>[[1, 2, 3, 4]]</b>
     */
    data?: number[][];
}
/**
 * @usedBy ThfChartComponent
 *
 * @description Interface da séries do Gráfico
 */
export interface ThfPieChartSeries {
    /**
     * Array de objetos, que deve implementar a interface ThfChartPieData, quando o tipo de gráfico for: pie, donut ou funnel.
     */
    data?: Array<ThfChartPieData>;
}
/**
 * @usedBy ThfChartComponent
 *
 * @description Interface das séries do Gráfico
 */
export interface ThfBarChartSeries {
    /** Nome da série que será visível na legenda do gráfico. */
    name?: string;
    /**
     * Array de números, onde o gráfico for do tipo area, bar, column, line, verticalLine, waterfall ou horizontalWaterfall.
     */
    data?: number[];
}
/**
 * @usedBy ThfChartComponent
 *
 * @description Interface da série do Area Chart
 *
 * @extends ThfBarChartSeries
 */
export interface ThfAreaChartSeries extends ThfBarChartSeries {
}
