/**
 * @usedBy ThfMenuComponent
 *
 * @description
 * Interface para os itens de menu do componente thf-menu
 *
 */
export interface ThfMenuItem {
    /** Texto do item de menu  */
    label: string;
    /** Link para redirecionamento no click do item do menu */
    link?: string;
    /** Ação personalizada para clique do item de menu */
    action?: string | Function;
    /** Lista de sub-items */
    subItems?: Array<ThfMenuItem>;
}
