/**
 * @description
 * Interface para as ações dos componentes thf-page-default e thf-page-list.
 *
 * @usedBy ThfPageDefaultComponent, ThfPageListComponent
 */
export interface ThfPageAction {
    /** Label utilizada nos botões de ações. */
    label: string;
    /** URL utilizada no redirecionamento das páginas.*/
    url?: string;
    /** Nome do ícone a ser utilizado no botão de ação. */
    icon?: string;
    /** Nome da ação ou a referência da mesma a ser executada. */
    action?: string | Function;
    /** Define se o botão de ação será habilitado ou não. */
    disabled?: boolean;
}
