/**
 * @description
 * Interface para o atributo filter do componente thf-page-list.
 *
 * @usedBy ThfPageListComponent
 */
export interface ThfPageFilter {
    /** Label a ser apresentada dentro do campo de filtro. */
    placeholder?: string;
    /** Nome da ação ou a referência da mesma a ser executada. */
    action?: string | Function;
    /** Nome do ngModel do campo de filtro. */
    ngModel?: string;
    /** Nome da ação ou a referência da mesma a ser executada quando for disparado o
     * evento de click na label 'Busca Avançada' */
    advancedAction?: string | Function;
}
