/**
 * @docsPrivate
 *
 * @usedBy ThfDialogService
 *
 * @description
 * Interface para o título e a mensagem do serviço thf-dialog.
 */
export interface ThfDialogOptions {
    /** Título da caixa de diálogo */
    title: string;
    /** Mensagem da caixa de diálogo */
    message: string;
}
/**
 * @usedBy ThfDialogService
 * @extends ThfDialogOptions
 *
 * @description
 *
 * Interface com as propriedades da caixa de diálogo de alerta do serviço thf-dialog.
 */
export interface ThfDialogAlertOptions extends ThfDialogOptions {
    /** Ação executada ao fechar o alerta pelo botão "OK" */
    ok?: Function;
}
/**
 * @usedBy ThfDialogService
 * @extends ThfDialogOptions
 *
 * @description
 *
 * Interface com as propriedades da caixa de diálogo de confirmação do serviço thf-dialog.
 */
export interface ThfDialogConfirmOptions extends ThfDialogOptions {
    /** Ação de confirmação da caixa de diálogo */
    confirm: Function;
    /** Ação de cancelamento da caixa de diálogo */
    cancel?: Function;
}
