/**
 * @description
 * Interface para a configuração do módulo ThfI18nModule
 *
 * @usedBy ThfI18nModule
 */
export interface ThfI18nConfig {
    /** Configurações padrões */
    default?: ThfI18nConfigDefault;
    /** Contextos.
    * Opção por constantes: Informe o nome do contexto recebendo um objeto com os idiomas suportados e o arquivo de literais.
    * Exemplo:
    *   general: {
    *     pt: GeneralPT,
    *     en: GeneralEN
    *   }
    *
    * Opção por serviço:
    *   hcm: {
    *     url: 'http://localhost:3000/api/translations/hcm/'
    *   }
    */
    contexts: object;
}
export interface ThfI18nConfigDefault {
    /** Idioma Padrão. Ao informar o idioma, o serviço ignora o idioma do Browser do usuário. */
    language?: string;
    /** Contexto Padrão. Este é o contexto que será considerado ao injetar o serviço e não especificar o contexto. */
    context?: string;
    /** Define se utilizará cache. Por padrão não utiliza. */
    cache?: boolean;
}
