import { ThfToasterOrientation } from './thf-toaster-base/thf-toaster-orientation.enum';
/**
 * @usedBy ThfNotificationService
 *
 * @description
 *
 * Interface para uso do serviço thf-notification.
 */
export interface ThfNotification {
    action?: Function;
    /** Label do botão quando houver uma ação definida. */
    actionLabel?: string;
    message: string;
    /**
     * Posição da notificação na página que pode ser ```Top``` (topo) ou ```Bottom```(rodapé),
     * por padrão será exibida na parte superior da página.
     **/
    orientation?: ThfToasterOrientation;
}
