/**
 * @description
 * Interface do componente thf-dropdown
 * @usedBy ThfDropdownComponent
 */
export interface IThfDropdownAction {
    /** Label a ser utilizada nos itens da lista de ações e/ou links */
    label: string;
    /** URL utilizado no redirecionamento das páginas. */
    url?: string;
    /** Ação a ser executada, podendo passar o nome ou a referencia da função */
    action?: string | Function;
    /** Desabilita a ação. Valor a ser informado deve retornar um booleano.*/
    disabled?: boolean;
}
